mapper.select:
SELECT booth_no, brochure_path, video_path, video_call_path, booth_introduction, company_no, exhibition_no, company_tel, booth_profile FROM BOOTH


	AND company_no = #{company_no}


	AND booth_no = #{booth_no}

mapper.update:
UPDATE BOOTH

	video_path = #{video_path}, video_call_path = #{video_call_path}, booth_introduction = #{booth_introduction}, company_tel = #{company_tel}
	
		brochure_path = #{brochure_path}
	
	
		,booth_profile = #{booth_profile}
	

 WHERE booth_no = #{booth_no}

UPDATE PRODUCT 

	product_name = #{product_name}, product_desc = #{product_desc}, product_mall = #{product_mall}, booth_no = #{booth_no}
	
		product_image_path = #{product_image_path}
	

	WHERE product_no = #{product_no}
